from collections import Counter


class Solution:
    def longestPalindrome(self, s: str) -> int:
        c = Counter(s)
        odds_m1 = [v-1 for v in c.values() if v % 2 != 0]
        sum_odds = sum(odds_m1) + 1 if odds_m1 else 0
        sum_evens = sum(v for v in c.values() if v % 2 == 0)
        return sum_evens + sum_odds


tests = [
    ""
]

sol = Solution()

for test in tests:
    print(sol.longestPalindrome(test))
